import 'bootstrap/dist/css/bootstrap.css';
import logo from './images/logo.png';
import chat from './images/chat.png';
import edit from './images/edit.png';
import user from './user.json';

import Header from './Header'

import React from 'react';
import {
  HashRouter as Router,
  Route,
  Link,
  NavLink
} from 'react-router-dom';

class Sidebar extends React.Component {
  render() {
    return (
      <div className="col-3">
      <div id="sidebar">
        <br />
        <br />
        <a href="#" className="list-group-item">Personal Information</a>
        <a href="./survey" className="list-group-item">Self-survey Form</a>
        <a href="./analysis" className="list-group-item">Profile Analysis</a>
        <a href="./matching" className="list-group-item">Mentor Matching</a>
      </div>
    </div>
    );
  }
}

class Body extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      editName:false, editUni:false, editYear:false, editProgram:false, editLocation:false, editInterest:false, editGpa:false};
  }
  edit() {
    this.setState({editName:true});



  }
  render() {
    if(this.state.editName)
    {user.name = <input type="text" value={this.props.user.username} />;}
    if(this.state.editUni)
    {this.props.user.university = <input type="text" value={this.props.user.username} />;}
    if(this.state.editYear)
    {this.props.user.studyYear = <input type="text" value={this.props.user.username} />;}
    if(this.state.editProgram)
    {this.props.user.program = <input type="text" value={this.props.user.username} />;}
    if(this.state.editLocation)
    {this.props.user.location = <input type="text" value={this.props.user.username} />;}
    if(this.state.editInterest)
    {this.props.user.username = <input type="text" value={this.props.user.username} />;}
    if(this.state.editName)
    {this.props.user.username = <input type="text" value={this.props.user.username} />;}
    return (
      <div className="col-9" id="profileInfo">
        <div className="row" id="profileImg">
          <img src={process.env.PUBLIC_URL + user.profilePic} className="rounded-circle profileImage" alt="profile"/>
          <div className="text-center" id="changeImage">Edit Profile Photo</div>
        </div>
        <div className="row" id="profileDetails">
          <div className="col-5 text-right" id="profilePrompt"><p>Name*: </p></div>
          <div className="col-6 text-left">{user.name}<img className="edit" onClick={() => {this.setState({editName:true});this.edit();}} src={edit} /></div>
        </div>
        <br/>
        <div className="row" id="profileDetails">
          <div className="col-5 text-right" id="profilePrompt"><p>University*: </p></div>
          <div className="col-6 text-left">{this.props.user.university} <img className="edit" onClick={this.edit.bind(this)} src={edit} /></div>
        </div>
        <br/>
        <div className="row" id="profileDetails">
          <div className="col-5 text-right" id="profilePrompt"><p>Year of Study*: </p></div>
          <div className="col-6 text-left">{this.props.user.studyYear} <img className="edit" onClick={this.edit.bind(this)} src={edit} /></div>
        </div>
        <br/>
        <div className="row" id="profileDetails">
          <div className="col-5 text-right" id="profilePrompt"><p>Program*: </p></div>
          <div className="col-6 text-left">{this.props.user.program} <img className="edit" onClick={this.edit.bind(this)} src={edit} /></div>
        </div>
        <br/>
        <div className="row" id="profileDetails">
          <div className="col-5 text-right" id="profilePrompt"><p>Location: </p></div>
          <div className="col-6 text-left">{this.props.user.location} <img className="edit" onClick={this.edit.bind(this)} src={edit} /></div>
        </div>
        <br/>
        <div className="row" id="profileDetails">
          <div className="col-5 text-right" id="profilePrompt"><p>Area of Interest: <br/><small>please split item by semicolons(;)</small></p></div>
          <div className="col-6 text-left">{this.props.user.interest.map(interest => <span className="interestTag">{interest}</span>)} <img className="edit" onClick={this.edit.bind(this)} src={edit} /></div>
        </div>
        <br/>
        <div className="row" id="profileDetails">
          <div className="col-5 text-right" id="profilePrompt"><p>CGPA: </p></div>
          <div className="col-6 text-left">{this.props.user.cgpa} <img className="edit" onClick={this.edit.bind(this)} src={edit} /></div>
        </div>
      </div>
    );
  }
}

class ProfilePhoto extends React.Component
{
  render ()
  {
    return (
      <div className="m-5">
        <img src={process.env.PUBLIC_URL + user.profilePic} className="rounded-circle profileImage" alt="profile"/>
      </div>
     );
  }
};

class Profile extends React.Component {
  constructor(props) {
    super(props);
    this.state = { user:{username: user.name, university: user.email, studyYear: user.studyYear,
      program: user.program, location: user.location,
      interest: user.areaOfInterest,
      cgpa: user.gpa} }
  }
  render() {
    return (
      <div className="container">
        <div className="row">
          <Header />
        </div>
        <div className="row" id="profileBody">
          <Sidebar />
          <Body user={this.state.user}/>
        </div>
      </div>
    );
  }
}

export default Profile;
