import 'bootstrap/dist/css/bootstrap.css';
import logo from './images/logo.png';
import chat from './images/chat.png';
import user from './user.json';
import './Settings.css';

import React from 'react';
import Modal from 'react-modal';
import {
  HashRouter as Router,
  Route,
  Link,
  NavLink
} from 'react-router-dom';

class Settings extends React.Component {
  render() {
    return(
      <div className="container" id="main">
      <div className="form-check">
      <label className="label-inline mr-2">Filter for posts: </label>
        <label className="checkbox-inline mr-2">
          <input type="checkbox" checked={this.state.fireChecked} onChange={(event) => this.setState({fireChecked: !this.state.fireChecked})} id="fire"/>
          <label for="fire">Fire</label>
        </label>
        <label className="checkbox-inline mr-2">
          <input type="checkbox" checked={this.state.floodChecked} onChange={(event) => this.setState({floodChecked: !this.state.floodChecked})} id="flood"/>
          <label for="flood">Flood</label>
        </label>
        <label className="checkbox-inline mr-2">
          <input type="checkbox" checked={this.state.powerChecked} onChange={(event) => this.setState({powerChecked: !this.state.powerChecked})} id="power"/>
          <label for="power">Power</label>
        </label>
        <label className="checkbox-inline mr-2">
          <input type="checkbox" checked={this.state.medicalChecked} onChange={(event) => this.setState({medicalChecked: !this.state.medicalChecked})} id="medical"/>
          <label for="medical">Medical</label>
        </label>
        <label className="checkbox-inline mr-2">
          <input type="checkbox" checked={this.state.lowChecked} onChange={(event) => this.setState({lowChecked: !this.state.lowChecked})} id="low"/>
          <label for="low">Low</label>
        </label>
        <label className="checkbox-inline mr-2">
          <input type="checkbox" checked={this.state.mediumChecked} onChange={(event) => this.setState({mediumChecked: !this.state.mediumChecked})} id="medium"/>
          <label for="medium">Medium</label>
        </label>
        <label className="checkbox-inline mr-2">
          <input type="checkbox" checked={this.state.highChecked} onChange={(event) => this.setState({highChecked: !this.state.highChecked})} id="high"/>
          <label for="high">High</label>
        </label>
        <label className="checkbox-inline">
          <input type="checkbox" checked={this.state.criticalChecked} onChange={(event) => this.setState({criticalChecked: !this.state.criticalChecked})} id="critical"/>
          <label for="critical">Critical</label>
        </label>
      </div>
      </div>
    );
  }
}

export default Settings;
