import 'bootstrap/dist/css/bootstrap.css';
import logo from './logo.png';
import chat from './chat.png';
import edit from './edit.png';
import user from './user.json';
import posts from './posts.json'
import './Explore.css';

import React from 'react';
import {
  HashRouter as Router,
  Route,
  Link,
  NavLink
} from 'react-router-dom';

class Explore extends React.Component {
  constructor(props) {
    super(props);
    this.state = { posts: [
      {user:"Sample User",
      time:"2020-01-01",
      content:"some sample content",
      likes:0,
      comments:["Hamilton, Ontario"],
      photo:"./profile01.jpg"}
    ], nextID: 0 };
    var i;
    for(i=0; i< posts.length; i++) {
      this.setState({posts:[...this.state.posts,posts[i]],nextID: this.state.nextID + 1});
    }
  }
  render() {
    return (
      <div className="container" id="main">
        <br/>
        <div className="row" id="announce">
          <div className="col-9" id="hotTopic"></div>
          <div className="col-2" id="welcome">
            <div className="row" id="mentor"></div>
            <div className="row" id="student"></div>
          </div>
        </div>
        <br />
        <div className="row" id="posts">
          <div className="col-2" id="post">{this.state.posts.user}</div>

          <div className="col-2" id="post"></div>
          <div className="col-2" id="post"></div>
          <div className="col-2" id="post"></div>
          <div className="col-2" id="post"></div>
          <div className="col-2" id="post"></div>
          <div className="col-2" id="post"></div>
          <div className="col-2" id="post"></div>
          <div className="col-2" id="post"></div>
          <div className="col-2" id="post"></div>
        </div>
      </div>
    );
  }
}

export default Explore;
